#coding=utf-8import loginFrameimport contentFrameclass GuiManager():    def __init__(self, UpdateUI, db_path):        self.UpdateUI = UpdateUI        #self.frameDict = {} # 用来装载已经创建的Frame对象        self.db_path = db_path;        self.user = {};    def GetFrame(self, type):        #frame = self.frameDict.get(type)        frame = None;        if frame is None:            frame = self.CreateFrame(type)            #self.frameDict[type] = frame        return frame    def CreateFrame(self, type):        if type == 0:            return loginFrame.LoginFrame(parent=None, id=type, UpdateUI=self.UpdateUI, UpdateInfo=self.UpdateInfo,db_path=self.db_path)        elif type == 1:            return contentFrame.ContentFrame(parent=None, id=type, UpdateUI=self.UpdateUI,db_path=self.db_path, user_dic=self.user)    def UpdateInfo(self, user):        self.user = user;