#coding=utf-8import wximport wx.gridfrom db_tool import DataBaseclass PswFrame(wx.Frame):    def __init__(self, parent=None, id=-1, UpdateUI=None):        wx.Frame.__init__(self, parent, id, title=u'密码查看器', size=(500, 300), pos=(500, 200))        self.themeColor = '#0a74f7'        self.UpdateUI = UpdateUI        self.db = DataBase();        self.usrs = self.db.get_user_psw_list()        self.InitUI() # 绘制UI界面            def InitUI(self):        panel = wx.Panel(self)        font = wx.Font(14, wx.FONTFAMILY_DEFAULT, wx.FONTSTYLE_NORMAL, wx.FONTWEIGHT_NORMAL, True)        row = [u'用户名',u'密码',u'密码更新频率',u'种子'];        type_user = [u'永不更新',u'年',u'季度',u'月',u'周',u'天',u'小时'];        for i in range(len(self.usrs)):            self.usrs[i][2] = type_user[self.usrs[i][2]];                grid = wx.grid.Grid(self,-1,(1,1),(495,295))        grid.CreateGrid(len(self.usrs),len(row))        for r in range(len(row)):            grid.SetColLabelValue(r, row[r])            for c in range(len(self.usrs)):                grid.SetCellValue(c, r, self.usrs[c][r])class MainAPP(wx.App):    def OnInit(self):        self.frame = PswFrame()        self.frame.Show()        return Truedef main():    app = MainAPP()    app.MainLoop()if __name__ == '__main__':    main()