#coding=utf-8import wximport guiManager as FrameManagerimport multiprocessingimport osclass MainAPP(wx.App):    def OnInit(self):        self.db_path = "%s%scis.db"%(os.getcwd(),os.sep);        #print self.db_path;        if not os.path.exists(self.db_path ):            dlg = wx.MessageDialog(None, u'数据文件缺失，启动失败','A Message Box',wx.OK | wx.ICON_INFORMATION);            dlg.ShowModal()            dlg.Destroy()            return False;        self.manager = FrameManager.GuiManager(self.UpdateUI,self.db_path)        self.frame = self.manager.GetFrame(0)        self.frame.Show()        return True    def UpdateUI(self, type):        #self.frame.Show(False)        self.frame.Destroy()        self.frame = self.manager.GetFrame(type)        self.frame.Show(True)def main():    multiprocessing.freeze_support();    app = MainAPP()    app.MainLoop()if __name__ == '__main__':    main()